const a=this;a.func={};a.makeTopHeader=async()=>{const dom=a.dom.querySelector(".topHeader");if(!dom)return false;dom.pageTitile=dom.querySelector(".title");dom.menu=dom.querySelector(".menu");dom.setTitle=async title=>{dom.pageTitile.innerHTML=title};return true};a.makeHeader=async()=>{const dom=a.dom.querySelector(".header");if(!dom)return false;dom.mobileMenu=dom.querySelector(".mobileMenu");dom.menu=dom.querySelector("nav.menu ul");dom.iconSize=dom.menu.querySelector("li a svg").getBoundingClientRect().width;dom.menu.querySelectorAll("li a").forEach(a=>{a.icon=a.querySelector("svg");dom.menu[a.id]=a;a.addEventListener("mouseover",()=>{a.icon.style.width=dom.iconSize+6+"px";a.icon.style.margin="-3px";a.icon.style.fill="var(--menuIconHoverColor)"});a.addEventListener("mouseout",()=>{a.icon.style.width=dom.iconSize+"px";a.icon.style.margin="auto";a.icon.style.fill="var(--menuIconColor)"})});return true};a.makeShopPage=async()=>{const dom=a.dom.querySelector(".shop");if(!dom)return false;dom.title=dom.querySelector(".header h1");dom.shortDescription=dom.querySelector(".header p");dom.filters=dom.querySelector(".filters");dom.products=dom.querySelector("#products");dom.description=dom.querySelector(".description");dom.filters.style.removeProperty("display");return true};