-- drop table if exists public.stock_quantity;
drop table if exists public.stock_quantity;

-- create table
CREATE TABLE IF NOT EXISTS public.stock_quantity
(
    id           UUID UNIQUE DEFAULT uuid_generate_v4() NOT NULL,

    product_id   UUID        DEFAULT NULL,
    quantity     INTEGER     DEFAULT NULL,
    warehouse_id UUID        DEFAULT NULL,

    created_at   TIMESTAMP   DEFAULT NOW()              NOT NULL,
    created_by   UUID        DEFAULT NULL,
    updated_at   TIMESTAMP   DEFAULT NOW()              NOT NULL,
    updated_by   UUID        DEFAULT NULL,

    CONSTRAINT stock_quantity_pkey PRIMARY KEY (id)
);

-- ownership and index
ALTER TABLE public.stock_quantity OWNER TO postgres;
CREATE INDEX stock_quantity_id ON public.stock_quantity (id);
CREATE INDEX stock_quantity_product_id ON public.stock_quantity (product_id);
CREATE INDEX stock_quantity_warehouse_id ON public.stock_quantity (warehouse_id);
CREATE INDEX stock_quantity_updated_at ON public.stock_quantity (updated_at);

-- add comments
COMMENT ON TABLE public.stock_quantity IS 'stock_quantity table';
COMMENT ON COLUMN public.stock_quantity.id IS 'Unique identifier';
COMMENT ON COLUMN public.stock_quantity.product_id IS 'Product identifier';
COMMENT ON COLUMN public.stock_quantity.quantity IS 'Quantity of product';
COMMENT ON COLUMN public.stock_quantity.warehouse_id IS 'Warehouse identifier';
COMMENT ON COLUMN public.stock_quantity.created_at IS 'Creation date';
COMMENT ON COLUMN public.stock_quantity.created_by IS 'Creator identifier';
COMMENT ON COLUMN public.stock_quantity.updated_at IS 'Update date';
COMMENT ON COLUMN public.stock_quantity.updated_by IS 'Updater identifier';

-- auto update updated_at
CREATE OR REPLACE TRIGGER stock_quantity_updated_at
    BEFORE UPDATE
    ON public.stock_quantity
    FOR EACH ROW
    EXECUTE FUNCTION update_update_at_column();

-- auto set created_by
CREATE OR REPLACE TRIGGER stock_quantity_created_by
    BEFORE INSERT
    ON public.stock_quantity
    FOR EACH ROW
    EXECUTE FUNCTION set_created_by_if_null();

-- auto set updated_by
CREATE OR REPLACE TRIGGER stock_quantity_updated_by
    BEFORE INSERT
    ON public.stock_quantity
    FOR EACH ROW
    EXECUTE FUNCTION set_updated_by_if_null();

